import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styles from './Signup.module.css';

function Signup() {

  // define ======================================================
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const nav = useNavigate();

  const togglePasswordVisibility = () => {
    setShowPassword((prevState) => !prevState);
  };

  const toggleConfirmPasswordVisibility = () => {
    setShowConfirmPassword((prevState) => !prevState);
  };

  const goBack = () => {
    return nav("../")
  }

  return (
    <div className={styles.container}>
      <div className={styles.textContainer}>
        <h1>
          Stack<br/>
          &nbsp;&nbsp;&nbsp;&nbsp;Smarter,<br/>
          Flow<br/>
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Better.
        </h1>
      </div>
      <div className={styles.formContainer}>
        <form className={styles.signupForm}>
          <div className={styles.inputGroup}>
            <label htmlFor="username" className={styles.label}>ID</label>
            <input
              type="text"
              id="username"
              placeholder="ÏïÑÏù¥Îîî"
              className={styles.inputField}
            />
          </div>
          <div className={styles.inputGroup}>
            <label htmlFor="password" className={styles.label}>Password</label>
            <div className={styles.passwordContainer}>
              <input
                type={showPassword ? 'text' : 'password'}
                id="password"
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏"
                className={styles.inputField}
              />
              <span 
                className={styles.showPassword} 
                onClick={togglePasswordVisibility}
              >
                {showPassword ? 'üîí' : 'üëÅÔ∏è'}
              </span>
            </div>
          </div>
          <div className={styles.inputGroup}>
            <label htmlFor="confirmPassword" className={styles.label}>Password Confirmation</label>
            <div className={styles.passwordContainer}>
              <input
                type={showConfirmPassword ? 'text' : 'password'}
                id="confirmPassword"
                placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏"
                className={styles.inputField}
              />
              <span 
                className={styles.showPassword} 
                onClick={toggleConfirmPasswordVisibility}
              >
                {showConfirmPassword ? 'üîí' : 'üëÅÔ∏è'}
              </span>
            </div>
          </div>
          <div className={styles.inputGroup}>
            <label htmlFor="StoreCode" className={styles.label}>Store Code</label>
            <input
              type="text"
              id="StoreCode"
              className={styles.inputField}
            />
          </div>
          <button type="submit" className={styles.signupButton}>Sign Up</button>
          <button 
            className={styles.backButton}
            onClick={goBack}>Îí§Î°ú Í∞ÄÍ∏∞</button>
        </form>
      </div>
    </div>
  );
}

export default Signup;
